package Day5;

public class Loopexercise4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int sum=0;
		for (int i=11;i<=100;i=i+1) {
			sum=sum+i;
			
		}
		System.out.println(sum);
		int l=sum%2;
		if (l==0){
			System.out.println("even");
			
		}
		else{
			System.out.println("odd ");
		}
		
		{
			
		}

	}

}
