package Day6;

public class Arrayexercise2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int [] x = new int [] {4,5,6,7,8};
		int sum=0;
		for (int i=0;i<=4;i=i+1) {
			sum=sum+x[i];
			
		}
		int l = sum/5;
		
		
		System.out.println(l);
		

	}

}
